package test.spyd;

public class unaryEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		
		System.out.println(a--);//10
		
		System.out.println(--a);//8
		
		boolean b=false;
		
		System.out.println(!b);
		
		System.out.println(~a);

	}

}
